import React, { useState } from 'react';

function ChatBot() {
    const [userInput, setUserInput] = useState('');
    const [messages, setMessages] = useState([]);

    const handleSendMessage = async () => {
        setMessages([...messages, { text: userInput, sender: 'user' }]);

        try {
            const response = await fetch('http://localhost:5000/send_message', {  // Python 백엔드의 엔드포인트 주소
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message: userInput })
            });

            if (!response.ok) {
                throw new Error('서버 응답 실패');
            }

            const data = await response.json();
            setMessages(prevMessages => [...prevMessages, { text: data.response, sender: 'assistant' }]);
        } catch (error) {
            setMessages(prevMessages => [...prevMessages, { text: '서버와 연결할 수 없습니다. 다시 시도해 주세요.', sender: 'assistant' }]);
            console.error('에러 발생:', error);
        }

        setUserInput('');
    };

    return (
        <div style={{ margin: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>엄마형 로봇과의 채팅</h1>
            <div style={{ border: '1px solid #ccc', padding: '10px', height: '300px', overflowY: 'scroll', marginBottom: '10px' }}>
                {messages.map((message, index) => (
                    <div key={index} style={{ margin: '5px 0', color: message.sender === 'user' ? 'blue' : 'green' }}>
                        {message.sender === 'user' ? `사용자: ${message.text}` : `로봇: ${message.text}`}
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                placeholder="메시지를 입력하세요..."
            />
            <button onClick={handleSendMessage}>전송</button>
        </div>
    );
}

export default ChatBot;
