import React, { useState } from 'react';

function ChatBot() {
    // 사용자 입력을 저장할 상태 변수
    const [userInput, setUserInput] = useState('');

    // 메시지 리스트를 저장할 상태 변수
    const [messages, setMessages] = useState([]);

    // 메시지를 전송하는 함수
    const handleSendMessage = async () => {
        // 사용자 메시지를 메시지 리스트에 추가
        setMessages([...messages, { text: userInput, sender: 'user' }]);

        try {
            // 서버에 사용자 메시지를 전송
            const response = await fetch('/send_message', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message: userInput }) // JSON 형태로 사용자 입력을 전송
            });

            // 서버로부터 응답받기
            if (!response.ok) {
                throw new Error('서버 응답 실패');
            }

            const data = await response.json();

            // 로봇의 응답 메시지를 메시지 리스트에 추가
            setMessages(prevMessages => [...prevMessages, { text: data.response, sender: 'assistant' }]);
        } catch (error) {
            // 에러 처리: 서버 연결 실패 메시지를 추가
            setMessages(prevMessages => [...prevMessages, { text: '서버와 연결할 수 없습니다. 다시 시도해 주세요.', sender: 'assistant' }]);
            console.error('에러 발생:', error);
        }

        // 입력창 초기화
        setUserInput('');
    };

    return (
        <div style={{ margin: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>엄마형 로봇과의 채팅</h1>
            <div style={{ 
                border: '1px solid #ccc', 
                padding: '10px', 
                height: '300px', 
                overflowY: 'scroll', 
                marginBottom: '10px' 
            }}>
                {/* 메시지 리스트를 맵핑하여 화면에 표시 */}
                {messages.map((message, index) => (
                    <div 
                        key={index} 
                        style={{ 
                            margin: '5px 0', 
                            color: message.sender === 'user' ? 'blue' : 'green' // 사용자와 로봇의 메시지 색상 다르게
                        }}>
                        {message.sender === 'user' 
                            ? `사용자: ${message.text}` 
                            : `로봇: ${message.text}`}
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={userInput} // 입력 필드의 값은 userInput 상태에 바인딩
                onChange={(e) => setUserInput(e.target.value)} // 입력 변화에 따라 상태 업데이트
                placeholder="메시지를 입력하세요..." // 입력 필드의 플레이스홀더
            />
            <button onClick={handleSendMessage}>전송</button> {/* 버튼 클릭 시 메시지 전송 */}
        </div>
    );
}

export default ChatBot; // 컴포넌트를 내보냄
